function moveCursorLeft(e){var t=document.getElementById(e),n=(t.selectionStart,t.selectionEnd);t.focus(),t.selectionStart=n-1,t.selectionEnd=n-1}function moveCursorRight(e){var t=document.getElementById(e),n=(t.selectionStart,t.selectionEnd);t.focus(),t.selectionStart=n+1,t.selectionEnd=n+1}function removePreviousChar(e){var t=document.getElementById(e),n=t.selectionStart,o=t.selectionEnd,s=n==o?n-1:n,l=t.value.substring(0,s)+t.value.substring(o);t.value=l,t.focus(),t.selectionStart=s,t.selectionEnd=s}function removeNextChar(e){var t=document.getElementById(e),n=t.selectionStart,o=t.selectionEnd,s=n==o?o+1:o,l=t.value.substring(0,n)+t.value.substring(s);t.value=l,t.focus(),t.selectionStart=n,t.selectionEnd=n}function insertAtCursorPosition(e,t){var n=document.getElementById(e),o=n.selectionStart,s=n.selectionEnd,l=n.value.substring(0,o)+t+n.value.substring(s);n.value=l,n.focus();var c=t.length;n.selectionStart=o+c,n.selectionEnd=o+c}function allClear(e){var t=document.getElementById(e);t.value="",t.focus()}